import{_ as e,o as r,c as a,Q as t}from"./chunks/framework.486b000b.js";const b=JSON.parse('{"title":"开始","description":"","frontmatter":{},"headers":[{"level":2,"title":"总览","slug":"总览","link":"#总览","children":[]},{"level":2,"title":"特性","slug":"特性","link":"#特性","children":[]},{"level":2,"title":"依赖","slug":"依赖","link":"#依赖","children":[]},{"level":2,"title":"交流","slug":"交流","link":"#交流","children":[]},{"level":2,"title":"License","slug":"License","link":"#License","children":[]}],"relativePath":"guide/index.md"}'),l={name:"guide/index.md"},i=t('<h1 id="开始" tabindex="-1">开始 <a class="header-anchor" href="#开始">¶</a></h1><h2 id="总览" tabindex="-1">总览 <a class="header-anchor" href="#总览">¶</a></h2><p>elegant-admin 是基于<a href="https://github.com/z-song/laravel-admin" target="_blank" rel="noreferrer">laravel-admin</a> 的升级版，具体请查看<a href="https://github.com/elegant-admin/provider/releases" target="_blank" rel="noreferrer">版本说明</a></p><p>laravel-admin 是一个可以快速帮你构建后台管理的工具，它提供的页面组件和表单元素等功能，能帮助你使用很少的代码就实现功能完善的后台管理功能。</p><div class="tip custom-block"><p class="custom-block-title">注意</p><p>当前版本(v1.8.<em>)需要安装PHP 7+和Laravel 8.</em></p></div><h2 id="特性" tabindex="-1">特性 <a class="header-anchor" href="#特性">¶</a></h2><ul><li>内置路由控制基于角色的权限系统</li><li><code>model-grid</code>支持快速构建数据表格</li><li><code>model-form</code>支持快速构建数据表单</li><li><code>model-tree</code>支持快速构建树状数据</li><li>内置40+种form元素组件、以及支持扩展组件</li><li>支持<code>Laravel</code>的多种模型关系</li><li><code>mysql</code>、<code>mongodb</code>、<code>pgsql</code>等多数据库支持</li><li>支持引入第三方前端库</li><li>数据库和artisan命令行工具的web实现</li><li>支持自定义图表</li><li>多种常用web组件</li><li>支持本地和oss文件上传</li></ul><h2 id="依赖" tabindex="-1">依赖 <a class="header-anchor" href="#依赖">¶</a></h2><p>elegant-admin 基于以下组件或者服务:</p><ul><li><a href="https://laravel.com/" target="_blank" rel="noreferrer">Laravel</a></li><li><a href="https://almsaeedstudio.com/" target="_blank" rel="noreferrer">AdminLTE</a></li><li><a href="http://eonasdan.github.io/bootstrap-datetimepicker/" target="_blank" rel="noreferrer">Datetimepicker</a></li><li><a href="http://fontawesome.io/" target="_blank" rel="noreferrer">font-awesome</a></li><li><a href="http://momentjs.com/" target="_blank" rel="noreferrer">moment</a></li><li><a href="https://www.google.com/maps" target="_blank" rel="noreferrer">Google map</a></li><li><a href="http://lbs.qq.com/" target="_blank" rel="noreferrer">Tencent map</a></li><li><a href="https://github.com/kartik-v/bootstrap-fileinput" target="_blank" rel="noreferrer">bootstrap-fileinput</a></li><li><a href="https://github.com/defunkt/jquery-pjax" target="_blank" rel="noreferrer">jquery-pjax</a></li><li><a href="http://dbushell.github.io/Nestable/" target="_blank" rel="noreferrer">Nestable</a></li><li><a href="http://codeseven.github.io/toastr/" target="_blank" rel="noreferrer">toastr</a></li><li><a href="http://github.com/vitalets/x-editable" target="_blank" rel="noreferrer">X-editable</a></li><li><a href="https://github.com/wpic/bootstrap-number-input" target="_blank" rel="noreferrer">bootstrap-number-input</a></li><li><a href="https://github.com/itsjavi/fontawesome-iconpicker" target="_blank" rel="noreferrer">fontawesome-iconpicker</a></li></ul><h2 id="交流" tabindex="-1">交流 <a class="header-anchor" href="#交流">¶</a></h2><p>QQ群:167834740</p><h2 id="License" tabindex="-1">License <a class="header-anchor" href="#License">¶</a></h2><p><code>elegant-admin</code> is licensed under <a href="https://elegant-admin.org/docs/zh/1.x/LICENSE" target="_blank" rel="noreferrer">The MIT License (MIT)</a>.</p>',14),o=[i];function n(s,c,h,d,p,f){return r(),a("div",null,o)}const g=e(l,[["render",n]]);export{b as __pageData,g as default};
